<div id="mainWrapper"><div ui-view="header" autoscroll="true" id="header"></div><div id="views"><div ng-repeat="page in pages" id="page_{{page.slug}}" on-finish-render="ngRepeatFinished" class="page"><div ui-view="{{page.slug}}" autoscroll="true" ng-bind-html="htmlSafe(page.content.rendered)" class="page-content"></div></div></div></div>